package com.vandersoncamp.transporte;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.CorsRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;@Configurationpublic class WebConfig {    @Bean    public WebMvcConfigurer getWebConfig() {        return new WebMvcConfigurer() {            @Override            public void addCorsMappings(CorsRegistry registry) {                registry.addMapping("/**")                        .allowCredentials(true)                        .allowedMethods("GET", "POST", "PUT", "DELETE")                        .allowedOrigins("http://localhost:9000")                        .maxAge(3600);            }        };    }}