package com.vandersoncamp.transporte.enuns;public enum TipoTransporteEnum {    ONIBUS(0, "Ônibus"),    LOTACAO(1, "Lotação");    private final int valor;    private final String descricao;    private TipoTransporteEnum(int valor, String descricao) {        this.valor = valor;        this.descricao = descricao;    }    public String getDescricao() {        return descricao;    }    public int getValor() {        return valor;    }}