package com.vandersoncamp.transporte.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.io.Serializable;@Entity@Table(name = "CLIENTES")@SequenceGenerator(name = "SEQ_CLIENTES", sequenceName = "SEQ_CLIENTES", initialValue = 1, allocationSize = 1)public class Cliente implements Serializable {    @Id    @Column(name = "ID")    @GeneratedValue(generator = "SEQ_CLIENTES", strategy = GenerationType.SEQUENCE)    private Long id;    @NotNull    @Size(max = 100)    @Column(name = "NOME", length = 100, nullable = false)    private String nome;    @NotNull    @Size(max = 11)    @Column(name = "DOCUMENTO", length = 14, nullable = false)    private String documento;    @Column(name = "TELEFONE", length = 10)    private Long telefone;    @Size(max = 100)    @Column(name = "EMAIL", length = 100)    private String email;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public String getDocumento() {        return documento;    }    public void setDocumento(String documento) {        this.documento = documento;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public Long getTelefone() {        return telefone;    }    public void setTelefone(Long telefone) {        this.telefone = telefone;    }}