package com.vandersoncamp.transporte.model;import com.vandersoncamp.transporte.enuns.EstadoEnum;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.io.Serializable;@Entity@Table(name = "ENDERECOS")@SequenceGenerator(name = "SEQ_ENDERECOS", sequenceName = "SEQ_ENDERECOS", initialValue = 1, allocationSize = 1)public class Endereco implements Serializable {    @Id    @Column(name = "ID")    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "SEQ_ENDERECOS")    private Long id;    @NotNull    @Size(max = 255)    @Column(name = "LOGRADOURO", length = 255, nullable = false)    private String logradouro;    @NotNull    @Size(max = 8)    @Column(name = "CEP", length = 8, nullable = false)    private String cep;    @NotNull    @Size(max = 255)    @Column(name = "BAIRRO", length = 255, nullable = false)    private String bairro;    @NotNull    @Size(max = 255)    @Column(name = "LOCALIDADE", length = 255, nullable = false)    private String localidade;    @NotNull    @Column(name = "UF", length = 2, nullable = false)    @Enumerated(EnumType.STRING)    private EstadoEnum uf;    @Column(name = "NUMERO")    private Long numero;    @Size(max = 255)    @Column(name = "COMPLEMENTO")    private String complemento;    @Size(max = 255)    @Column(name = "UNIDADE")    private String unidade;    @Column(name = "IBGE")    private Long ibge;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getLogradouro() {        return logradouro;    }    public void setLogradouro(String logradouro) {        this.logradouro = logradouro;    }    public String getCep() {        return cep;    }    public void setCep(String cep) {        this.cep = cep;    }    public String getBairro() {        return bairro;    }    public void setBairro(String bairro) {        this.bairro = bairro;    }    public String getLocalidade() {        return localidade;    }    public void setLocalidade(String localidade) {        this.localidade = localidade;    }    public EstadoEnum getUf() {        return uf;    }    public void setUf(EstadoEnum uf) {        this.uf = uf;    }    public String getUnidade() {        return unidade;    }    public void setUnidade(String unidade) {        this.unidade = unidade;    }    public Long getIbge() {        return ibge;    }    public void setIbge(Long ibge) {        this.ibge = ibge;    }    public Long getNumero() {        return numero;    }    public void setNumero(Long numero) {        this.numero = numero;    }    public String getComplemento() {        return complemento;    }    public void setComplemento(String complemento) {        this.complemento = complemento;    }}