package com.vandersoncamp.transporte.repository;import com.vandersoncamp.transporte.model.ClienteLinha;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface ClienteLinhaRepository extends JpaRepository<ClienteLinha, Long> {    @Query("select bean from ClienteLinha bean where cliente.id = :idCliente")    Iterable<ClienteLinha> findLinhasByCliente(@Param("idCliente") Long idCliente);}