package com.vandersoncamp.transporte.repository;import com.vandersoncamp.transporte.model.ClienteLinha;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface ClienteLinhaRepository extends JpaRepository<ClienteLinha, Long> {    @Query("SELECT bean FROM ClienteLinha bean WHERE cliente.id = :idCliente")    Iterable<ClienteLinha> findLinhasByCliente(@Param("idCliente") Long idCliente);    @Query(value = "SELECT CASE  WHEN count(a)> 0 THEN true ELSE false END FROM ClienteLinha a WHERE a.cliente.id = :idCliente AND a.linhaOnibus.id = :idLinhaOnibus")    boolean existsByClienteOnibus(@Param("idCliente") Long idCliente, @Param("idLinhaOnibus") Long idLinhaOnibus);    @Query(value = "SELECT CASE  WHEN count(a)> 0 THEN true ELSE false END FROM ClienteLinha a WHERE a.cliente.id = :idCliente AND a.linhaLotacao.id = :idLinhaLotacao")    boolean existsByClienteLotacao(@Param("idCliente") Long idCliente, @Param("idLinhaLotacao") Long idLinhaLotacao);}