package com.vandersoncamp.transporte.repository;import com.vandersoncamp.transporte.model.Cliente;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface ClienteRepository extends JpaRepository<Cliente, Long> {    Iterable<Cliente> findByNome(String nome);    Optional<Cliente> findByDocumento(String documento);}