package com.vandersoncamp.transporte.repository;import com.vandersoncamp.transporte.model.Cliente;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface ClienteRepository extends CrudRepository<Cliente, Long> {    Iterable<Cliente> findByNome(String nome);    Optional<Cliente> findByDocumento(String documento);}