package com.vandersoncamp.transporte.repository;import com.vandersoncamp.transporte.model.LinhaOnibus;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface LinhaOnibusRepository extends JpaRepository<LinhaOnibus, Long> {    @Query("SELECT c FROM LinhaOnibus c WHERE lower(c.nome) LIKE lower(CONCAT('%', :nome,'%'))")    Iterable<LinhaOnibus> findByNome(@Param("nome") String nome);}