package com.vandersoncamp.transporte.resources;import com.vandersoncamp.transporte.dto.ClienteLinhaDTO;import com.vandersoncamp.transporte.model.ClienteLinha;import com.vandersoncamp.transporte.service.clientelinha.ClienteLinhaService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("cliente-linha")public class ClienteLinhaResource {    @Autowired    private ClienteLinhaService service;    @PostMapping    @ResponseStatus(HttpStatus.CREATED)    public void insert(@RequestBody ClienteLinhaDTO dto) {        service.save(dto);    }    @GetMapping("cliente/{id}")    public Iterable<ClienteLinha> findLinhasByCliente(@PathVariable("id") Long idCliente) {        return service.findLinhasByCliente(idCliente);    }}