package com.vandersoncamp.transporte.service.clientelinha;import com.vandersoncamp.transporte.model.ClienteLinha;import com.vandersoncamp.transporte.repository.ClienteLinhaRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class ClienteLinhaService {    @Autowired    private ClienteLinhaRepository repository;    public ClienteLinhaService(ClienteLinhaRepository repository) {        this.repository = repository;    }    public Iterable<ClienteLinha> findAll() {        return repository.findAll();    }    public ClienteLinha save(ClienteLinha endereco) {        return repository.save(endereco);    }    public ClienteLinha findById(Long id) {        return repository.findById(id).orElse(null);    }    public void remove(Long id) {        repository.deleteById(id);    }    public Iterable<ClienteLinha> findLinhasByCliente(Long idCliente) {        return repository.findLinhasByCliente(idCliente);    }}