package com.vandersoncamp.transporte.service.endereco;import com.vandersoncamp.transporte.model.Endereco;import com.vandersoncamp.transporte.repository.EnderecoRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class EnderecoService {    @Autowired    private EnderecoRepository repository;    public EnderecoService(EnderecoRepository repository) {        this.repository = repository;    }    public Iterable<Endereco> findAll() {        return repository.findAll();    }    public Endereco save(Endereco endereco) {        /**         * Usuario usuario = (Usuario) SecurityContextHolder.getContext().getAuthentication().getPrincipal();         * endereco.setUsuarioAlteracao(usuario);         */        return repository.save(endereco);    }    public Endereco findById(Long id) {        return repository.findById(id).orElse(null);    }    public void remove(Long id) {        repository.deleteById(id);    }    public Iterable<Endereco> findByLogradouro(String logradouro) {        return repository.findByLogradouro(logradouro);    }    public Endereco findByCep(String cep) {        return repository.findByCep(cep);    }}