package com.vandersoncamp.transporte.service.linhaonibus;import com.vandersoncamp.transporte.dto.LinhaDTO;import com.vandersoncamp.transporte.model.LinhaOnibus;import org.springframework.stereotype.Service;@Servicepublic class LinhaDTOService {    public LinhaOnibus toEntity(LinhaDTO dto) {        LinhaOnibus entity = new LinhaOnibus();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }}