package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.dto.LinhaDTO;import com.vandersoncamp.transporte.model.LinhaLotacao;import com.vandersoncamp.transporte.model.LinhaOnibus;import org.springframework.stereotype.Service;@Servicepublic class LinhaDTOService {    public LinhaOnibus toEntityOnibus(LinhaDTO dto) {        LinhaOnibus entity = new LinhaOnibus();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }    public LinhaLotacao toEntityLotacao(LinhaDTO dto) {        LinhaLotacao entity = new LinhaLotacao();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }}