package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.dto.LinhaDTO;import com.vandersoncamp.transporte.enuns.TipoTransporteEnum;import com.vandersoncamp.transporte.model.Cliente;import com.vandersoncamp.transporte.model.ClienteLinha;import com.vandersoncamp.transporte.model.LinhaLotacao;import com.vandersoncamp.transporte.model.LinhaOnibus;import com.vandersoncamp.transporte.repository.ClienteRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class LinhaDtoService {    @Autowired    private ClienteRepository clienteRepository;    public LinhaOnibus toEntityOnibus(LinhaDTO dto) {        LinhaOnibus entity = new LinhaOnibus();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }    public LinhaLotacao toEntityLotacao(LinhaDTO dto) {        LinhaLotacao entity = new LinhaLotacao();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }    public ClienteLinha toLinhaOnibus(LinhaOnibus linhaOnibus, Cliente cliente) {        ClienteLinha linha = new ClienteLinha();        linha.setCliente(getCliente(cliente.getId()));        linha.setTipoTransporte(TipoTransporteEnum.ONIBUS);        linha.setLinhaOnibus(linhaOnibus);        return linha;    }    public ClienteLinha toLinhaLotacao(LinhaLotacao linhaLotacao, Cliente cliente) {        ClienteLinha linha = new ClienteLinha();        linha.setCliente(getCliente(cliente.getId()));        linha.setTipoTransporte(TipoTransporteEnum.LOTACAO);        linha.setLinhaLotacao(linhaLotacao);        return linha;    }    private Cliente getCliente(Long idCliente) {        return clienteRepository.findById(idCliente).get();    }}