package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.dto.LinhaDTO;import com.vandersoncamp.transporte.enuns.TipoTransporteEnum;import com.vandersoncamp.transporte.model.Cliente;import com.vandersoncamp.transporte.model.ClienteLinha;import com.vandersoncamp.transporte.model.LinhaLotacao;import com.vandersoncamp.transporte.model.LinhaOnibus;import org.springframework.stereotype.Service;@Servicepublic class LinhaDtoService {    public LinhaOnibus toEntityOnibus(LinhaDTO dto) {        LinhaOnibus entity = new LinhaOnibus();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }    public LinhaLotacao toEntityLotacao(LinhaDTO dto) {        LinhaLotacao entity = new LinhaLotacao();        entity.setId(Long.parseLong(dto.getId()));        entity.setCodigo(dto.getCodigo());        entity.setNome(dto.getNome());        return entity;    }    public ClienteLinha toClienteLinhaOnibus(LinhaOnibus linhaOnibus, Cliente cliente) {        ClienteLinha linha = new ClienteLinha();        linha.setCliente(cliente);        linha.setTipoTransporte(TipoTransporteEnum.ONIBUS);        linha.setLinhaOnibus(linhaOnibus);        return linha;    }    public ClienteLinha toClienteLinhaLotacao(LinhaLotacao linhaLotacao, Cliente cliente) {        ClienteLinha linha = new ClienteLinha();        linha.setCliente(cliente);        linha.setTipoTransporte(TipoTransporteEnum.LOTACAO);        linha.setLinhaLotacao(linhaLotacao);        return linha;    }}