package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.model.LinhaLotacao;import com.vandersoncamp.transporte.repository.LinhaLotacaoRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class LinhaLotacaoService {    @Autowired    private LinhaLotacaoRepository repository;    public LinhaLotacaoService(LinhaLotacaoRepository repository) {        this.repository = repository;    }    public Iterable<LinhaLotacao> findAll() {        return repository.findAll();    }    public LinhaLotacao save(LinhaLotacao linhaLotacao) {        return repository.save(linhaLotacao);    }    public Optional<LinhaLotacao> findById(Long id) {        return repository.findById(id);    }    public void integrarLinhaLotacao(List<LinhaLotacao> linhas) {        linhas.forEach(this::geraLinhaLotacao);    }    private void geraLinhaLotacao(LinhaLotacao linha) {        Optional<LinhaLotacao> linhaLotacao = findById(linha.getId());        if (!linhaLotacao.isPresent()) {            save(linha);            return;        }        if (!linhaLotacao.get().equals(linha)) {            LinhaLotacao novalinha = new LinhaLotacao();            novalinha.setId(linha.getId());            novalinha.setCodigo(linha.getCodigo());            novalinha.setNome(linha.getNome());            save(novalinha);        }    }}