package com.vandersoncamp.transporte.utils;import br.com.caelum.stella.ValidationMessage;import br.com.caelum.stella.validation.CPFValidator;import java.util.List;public class ValidaCpf {    public static boolean valida(String cpf) {        CPFValidator cpfValidator = new CPFValidator();        List<ValidationMessage> erros = cpfValidator.invalidMessagesFor(cpf);        return erros.isEmpty();    }}