CREATE SEQUENCE SEQ_CLIENTES AS BIGINT START WITH 1 INCREMENT BY 1;CREATE TABLE CLIENTES (  ID BIGINT GENERATED BY DEFAULT AS SEQUENCE SEQ_CLIENTES PRIMARY KEY,  NOME VARCHAR(100) NOT NULL,  NOME_FANTASIA VARCHAR(100),  DOCUMENTO VARCHAR(14) NOT NULL,  INSCRICAO_ESTADUAL VARCHAR(10),  REGISTRO_GERAL VARCHAR(9),  CELULAR BIGINT,  TELEFONE BIGINT,  EMAIL VARCHAR(100),  DATA_CADASTRO DATE NOT NULL,  OBSERVACAO VARCHAR(255),);CREATE SEQUENCE SEQ_ENDERECOS AS BIGINT START WITH 1 INCREMENT BY 1;CREATE TABLE ENDERECOS (  ID BIGINT GENERATED BY DEFAULT AS SEQUENCE SEQ_ENDERECOS PRIMARY KEY,  CEP VARCHAR(8) NOT NULL,  LOGRADOURO VARCHAR(255) NOT NULL,  BAIRRO VARCHAR(255) NOT NULL,  LOCALIDADE VARCHAR(255) NOT NULL,  UF VARCHAR(2) NOT NULL,  NUMERO INT,  COMPLEMENTO VARCHAR(255),  UNIDADE VARCHAR(255),  IBGE BIGINT);