package com.vandersoncamp.transporte.enuns;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.test.context.junit4.SpringRunner;import static org.junit.jupiter.api.Assertions.assertEquals;@RunWith(SpringRunner.class)public class EstadoEnumTest {    private EstadoEnum estadoEnum;    @Before    public void setUp() {        estadoEnum = EstadoEnum.DF;    }    @Test    public void estadoEnum() {        assertEquals(estadoEnum.getCodigo(), "53");        assertEquals(estadoEnum.getNome(), "Distrito Federal");        assertEquals(estadoEnum.getCodigoIbge(), 53);    }}