package com.vandersoncamp.transporte.resource;import com.vandersoncamp.transporte.TransporteApplicationTests;import org.junit.Test;import org.springframework.boot.web.server.LocalServerPort;import org.springframework.http.HttpStatus;import static io.restassured.RestAssured.given;import static io.restassured.http.ContentType.JSON;import static org.hamcrest.Matchers.empty;import static org.hamcrest.Matchers.not;public class LinhaLotacaoResourceTest extends TransporteApplicationTests {    @LocalServerPort    private int port;    @Test    public void pesquisarLinhaLotacao() {        given().port(port).contentType(JSON)                .when()                .get("/linha-lotacao/consultar")                .then()                .statusCode(HttpStatus.OK.value())                .body("results", not(empty()));    }}