package com.vandersoncamp.transporte.resources;import com.vandersoncamp.transporte.TransporteApplicationTests;import org.junit.Test;import org.springframework.boot.web.server.LocalServerPort;import org.springframework.http.HttpStatus;import static io.restassured.RestAssured.given;import static io.restassured.http.ContentType.JSON;import static org.hamcrest.Matchers.empty;import static org.hamcrest.Matchers.not;public class LinhaOnibusResourceTest extends TransporteApplicationTests {    @LocalServerPort    private int port;    @Test    public void linhaOnibusFindAll() {        given().port(port).contentType(JSON)                .when()                .get("/linha-onibus/consultar")                .then()                .statusCode(HttpStatus.OK.value())                .body("results", not(empty()));    }    @Test    public void linhaOnibusFindByNome() {        given().port(port).contentType(JSON)                .queryParam("nome", "Onibus escolar")                .get("/linha-onibus/filter")                .then()                .statusCode(HttpStatus.OK.value())                .body("results", not(empty()));    }    @Test    public void linhaOnibusFindByNomeEmpty() {        given().port(port).contentType(JSON)                .queryParam("nome", "Sem dados")                .get("/linha-onibus/filter")                .then()                .statusCode(HttpStatus.OK.value())                .body("results", empty());    }    @Test    public void linhaOnibusIntegrarLinhas() {        given().port(port).contentType(JSON)                .when()                .post("/linha-onibus/integrar")                .then()                .statusCode(HttpStatus.OK.value());    }}