package com.vandersoncamp.transporte.service.endereco;import com.vandersoncamp.transporte.enuns.EstadoEnum;import com.vandersoncamp.transporte.model.Endereco;import com.vandersoncamp.transporte.repository.EnderecoRepository;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.boot.test.mock.mockito.MockBean;import org.springframework.test.context.junit4.SpringRunner;import java.util.Optional;import static org.mockito.Mockito.verify;import static org.mockito.Mockito.when;@RunWith(SpringRunner.class)public class EnderecoServiceTest {    @MockBean    private EnderecoRepository repository;    private EnderecoService service;    private Endereco endereco;    private Endereco getEndereco() {        Endereco endereco = new Endereco();        endereco.setId(1L);        endereco.setBairro("Bairro teste");        endereco.setCep("88808456");        endereco.setLocalidade("Cidade teste");        endereco.setComplemento("Complemento teste");        endereco.setUf(EstadoEnum.SC);        endereco.setLogradouro("Logradouro teste");        endereco.setNumero(12L);        endereco.setIbge(4204608L);        endereco.setUnidade("Unidade");        return endereco;    }    @Before    public void setUp() {        service = new EnderecoService(repository);        endereco = getEndereco();        when(repository.findById(endereco.getId())).thenReturn(Optional.of(new Endereco()));    }    @Test    public void enderecoSave() {        service.save(endereco);        verify(repository).save(endereco);    }    @Test    public void enderecoFindAll() {        service.findAll();        verify(repository).findAll();    }    @Test    public void enderecoFindById() {        service.findById(endereco.getId());        verify(repository).findById(endereco.getId());    }    @Test    public void enderecoDelete() {        service.remove(endereco.getId());        verify(repository).deleteById(endereco.getId());    }    @Test    public void enderecoFindByLogradouro() {        service.findByLogradouro(endereco.getLogradouro());        verify(repository).findByLogradouro(endereco.getLogradouro());    }    @Test    public void enderecoFindByCep() {        service.findByCep(endereco.getCep());        verify(repository).findByCep(endereco.getCep());    }}