package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.dto.LinhaDTO;import com.vandersoncamp.transporte.model.Cliente;import com.vandersoncamp.transporte.model.LinhaLotacao;import com.vandersoncamp.transporte.model.LinhaOnibus;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.test.context.junit4.SpringRunner;import static org.mockito.Mockito.mock;import static org.mockito.Mockito.when;@RunWith(SpringRunner.class)public class LinhaDtoServiceTest {    private LinhaDtoService service;    private LinhaDTO mockLinhaDto() {        LinhaDTO linha = mock(LinhaDTO.class);        when(linha.getId()).thenReturn("1");        when(linha.getCodigo()).thenReturn("145-9");        when(linha.getNome()).thenReturn("Linha transporte");        return linha;    }    private Cliente getCliente() {        Cliente cliente = new Cliente();        cliente.setId(1L);        cliente.setDocumento("49765258216");        cliente.setEmail("email@email.com.br");        cliente.setNome("Teste cliente");        cliente.setTelefone(767678689L);        return cliente;    }    private LinhaOnibus getLinhaOnibus() {        LinhaOnibus linhaOnibus = new LinhaOnibus();        linhaOnibus.setId(1L);        linhaOnibus.setNome("Linha Avenida");        linhaOnibus.setCodigo("367-2");        return linhaOnibus;    }    private LinhaLotacao getLinhaLotacao() {        LinhaLotacao linhaLotacao = new LinhaLotacao();        linhaLotacao.setId(1L);        linhaLotacao.setNome("Lotação parque");        linhaLotacao.setCodigo("987A-1");        return linhaLotacao;    }    @Before    public void setUp() {        service = new LinhaDtoService();    }    @Test    public void linhaDtoToEntityOnibus() {        service.toEntityOnibus(mockLinhaDto());    }    @Test    public void linhaDtoToEntityLotacao() {        service.toEntityLotacao(mockLinhaDto());    }    @Test    public void linhaDtoToClienteLinhaOnibus() {        service.toClienteLinhaOnibus(getLinhaOnibus(), getCliente());    }    @Test    public void linhaDtoToClienteLinhaLotacao() {        service.toClienteLinhaLotacao(getLinhaLotacao(), getCliente());    }}