package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.model.LinhaLotacao;import com.vandersoncamp.transporte.repository.LinhaLotacaoRepository;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.boot.test.mock.mockito.MockBean;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;import java.util.List;import java.util.Optional;import static org.mockito.Mockito.verify;import static org.mockito.Mockito.when;@RunWith(SpringRunner.class)public class LinhaLotacaoServiceTest {    @MockBean    private LinhaLotacaoRepository repository;    private LinhaLotacaoService service;    private LinhaLotacao linhaLotacao;    private LinhaLotacao getLinhaLotacao() {        LinhaLotacao linhaLotacao = new LinhaLotacao();        linhaLotacao.setId(1L);        linhaLotacao.setNome("Lotação parque");        linhaLotacao.setCodigo("987A-1");        return linhaLotacao;    }    @Before    public void setUp() {        service = new LinhaLotacaoService(repository);        linhaLotacao = getLinhaLotacao();        when(repository.findById(linhaLotacao.getId())).thenReturn(Optional.of(new LinhaLotacao()));    }    @Test    public void linhaLotacaoSave() {        service.save(linhaLotacao);        verify(repository).save(linhaLotacao);    }    @Test    public void linhaLotacaoFindAll() {        service.findAll();        verify(repository).findAll();    }    @Test    public void linhaLotacaoFindById() {        service.findById(linhaLotacao.getId());        verify(repository).findById(linhaLotacao.getId());    }    @Test    public void linhaLotacaoIntegrarLinhaLotacao() {        List<LinhaLotacao> list = new ArrayList<>();        list.add(getLinhaLotacao());        service.integrarLinhaLotacao(list);    }    @Test    public void linhaLotacaoGeraLinhaLotacao() {        service.geraLinhaLotacao(getLinhaLotacao());    }    @Test    public void linhaLotacaoGeraLinhaLotacaoNull() {        service.geraLinhaLotacao(new LinhaLotacao());    }}