package com.vandersoncamp.transporte.service.linhatransporte;import com.vandersoncamp.transporte.model.LinhaOnibus;import com.vandersoncamp.transporte.repository.LinhaOnibusRepository;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.boot.test.mock.mockito.MockBean;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;import java.util.List;import java.util.Optional;import static org.mockito.Mockito.verify;import static org.mockito.Mockito.when;@RunWith(SpringRunner.class)public class LinhaOnibusServiceTest {    @MockBean    private LinhaOnibusRepository repository;    private LinhaOnibusService service;    private LinhaOnibus linhaOnibus;    private LinhaOnibus getLinhaOnibus() {        LinhaOnibus linhaOnibus = new LinhaOnibus();        linhaOnibus.setId(1L);        linhaOnibus.setNome("Linha Avenida");        linhaOnibus.setCodigo("367-2");        return linhaOnibus;    }    @Before    public void setUp() {        service = new LinhaOnibusService(repository);        linhaOnibus = getLinhaOnibus();        when(repository.findById(linhaOnibus.getId())).thenReturn(Optional.of(new LinhaOnibus()));    }    @Test    public void linhaOnibusSave() {        service.save(linhaOnibus);        verify(repository).save(linhaOnibus);    }    @Test    public void linhaOnibusFindAll() {        service.findAll();        verify(repository).findAll();    }    @Test    public void linhaOnibusFindById() {        service.findById(linhaOnibus.getId());        verify(repository).findById(linhaOnibus.getId());    }    @Test    public void linhaOnibusfindByNome() {        service.findByNome(linhaOnibus.getNome());        verify(repository).findByNome(linhaOnibus.getNome());    }    @Test    public void linhaOnibusIntegrarLinhaOnibus() {        List<LinhaOnibus> list = new ArrayList<>();        list.add(getLinhaOnibus());        service.integrarLinhaOnibus(list);    }    @Test    public void linhaOnibusGeraLinhaOnibus() {        service.geraLinhaOnibus(getLinhaOnibus());    }    @Test    public void linhaOnibusGeraLinhaOnibusNull() {        service.geraLinhaOnibus(new LinhaOnibus());    }}